package day04_relateionalOperator;

public class AssingmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 5;
		
		int b = a;
		
		System.out.println(a);
		
		System.out.println("Value of a is"  + a);
		
		System.out.println(b);
		
		System.out.println("Value of b is"  + b);
		
		//         a = 5 + 3
		a += 3; // a = a + 3
		
		System.out.println("Value of a is"  + a); //8
		
		
       a += 10; // a = a+ 10
		
		System.out.println("Value of a is"  + a); //18
		
		// -=
		
		a -= 5; // a= a-5
		
		
		System.out.println("Value of a is"  + a); //13
		
		// Value b still 5
		
		System.out.println(b);
		
		b *= 5; // b = b * 5
		System.out.println(b); // 25
		
		b /= 5; // b = b / 5
		 
		System.out.println(" Line number 59 + < " + b+ " > "); // 5
		
		b %= 2; // b % 2
		
		
		System.out.println(b); // 1
		
		b = a;
		
		
		System.out.println(" Line number 67 + < " + b+ " > "); // 13
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
