package day04_relateionalOperator;

public class ExecutionOrder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =3;
		
		int b =5;
		
		int c =2;
		
		int result = a + b * c ; //13
		
		System.out.println(result);
		
		System.out.println(b + a + c); // 10
		
		System.out.println(b * c / a); //3.33
		
		System.out.println(b * c - a); //7
		
		System.out.println(a * (a+b) - c-a*a); //13
		
		
		
		
		
		
	}

}
