package day05_ifStatment;

public class IfStatmentIntro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int count = 2;
		
		int total = 4;
		
//		if ( your experssion) {}
		
		
		if (count < 3) {
			
			total = 0;
		}else {
			
			total = total + count ;
			
		}
		
		System.out.println(total);
		
		
		
		
		
		
	}// end method

}// end class
