package day12_StringContinue;

public class StringReview {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//            0123
		String str = "Java";
		
		//constructer = create empty string
		
		String word = new String();
		
		//parameter constructer
		
		String word2 = new String("Java");
		
		String word3 = ""; //empty string

		System.out.println(str.length()); //4
		
		System.out.println(word.length()); //0
		
		System.out.println(word2.length()); //4
		
		System.out.println(word3.length()); //0
		
		System.out.println(str.charAt(0));
		
		System.out.println(str.charAt(1));
		
		System.out.println(str.charAt(2));
		
		System.out.println(str.charAt(3));
		
		
	}

}
